$(function(){var e=200;$window=$(window);$link=$("#scrollToTop");$link.click(function(){$("html, body").animate({scrollTop:0},"slow");return false});if(!$window.scrollTop()){$link.hide()}$window.scroll(function(){var t=$(window).scrollTop();$("header.main").css("background-position","center bottom "+ -t/5+"px");$("header.main>.content").css("opacity","1"-t/800);if(t<=e){$link.fadeOut("fast")}else{$link.fadeIn("fast")}})})